import React from 'react'
// import * as BooksAPI from './BooksAPI'
import '../App.css'

import { Link } from 'react-router-dom';
import { render } from 'react-dom';

class Book extends React.Component {

    constructor(props){
        super(props);
        
    }

        

    // changeBookShelf = (ev) => {
    //     let changedShelfBook = this.props.book;
    //     changedShelfBook.shelf = ev.target.value;
    //     this.props.onChangeBookShelf(changedShelfBook)
    // }

    changeBookShelf = (ev) => {
        let newShelf = ev.target.value;
        this.props.onChangeBookShelf(this.props.book, newShelf)
    }

    render(){

        const { book }  = this.props;

        return (
            <li>
                <div className="book">
                    <div className="book-top">
                        <div className="book-cover" style={{ width: 128, height: 193, backgroundImage: `url(${book.imageLinks.thumbnail})` }}></div>
                        <div className="book-shelf-changer">
                        <select onChange={ this.changeBookShelf } value={book.shelf}>
                            <option value="move" disabled>Move to...</option>
                            <option value="currentlyReading">Currently Reading</option>
                            <option value="wantToRead">Want to Read</option>
                            <option value="read">Read</option>
                            <option value="none">None</option>
                        </select>
                        </div>
                    </div>
                    <div className="book-title">{book.title}</div>
                    <div className="book-authors">
                        {
                            book.authors.map((author)=>(
                                `${author}`
                            ))
                        }
                    </div>
                </div>
            </li>
        )
    }

}


export default Book;
